import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    redirect: '/home/MarketShop' 
  },

  {
    path: '/userhome',
    name: 'userhome',
    component: () => import('@/views/chat/ChatMemu.vue'),
    children: [
      {
        path:'user',
        name:'user',
        component:() => import('@/views/user/UserHome.vue')
      }
    ]

  }
]

const router = new VueRouter({
  routes
})

//全局前置路由守卫————初始化的时候被调用、每次路由切换之前被调用
router.beforeEach((to, from, next) => {
  //如果路由需要跳转
  if (to.meta.isAuth) {
      //如果钱包已经连接，放行
