export default {
  data() {
    return {
      //****聊天栏******//
      isOpen: false,
      UserImageList: [],
      avatar: "",
      //*********************//
      //方法
      echartChange: null,
      //连接钱包
      user: {
        encryptedPassword: ""
      },
      changeingAccount: false,
      anmiate1: true,
      animate2: false,
      animate3: false,
      animate4: false,
      animate5: false,
      EmpowerSignForm: {
        sp1: "",
        sp2: "",
        sp3: ""
      },
      isGetToken: false,
      CopyTips: "点击复制",
      CopySuccess: "复制成功！",
      isCopy: false,
      isRepeatClick: true,
      newMnemonic: "",
      isUnlocked: false
    };
  },
  async mounted() {
    if (window.ethereum != undefined) {
      await window.ethereum._metamask.isUnlocked().then(re => this.isUnlocked = re);
      window.ethereum.on("accountsChanged", async () => {
        window.location.reload();
      });
      window.ethereum.on("chainChanged", () => {
        window.location.reload();
        this.$notify({
          title: "已切换链",
          type: "warning",
          position: "top-left",
          offset: 200
        });
      });
      if (!this.$store.state.isconnect && this.isUnlocked) {
        await this.connectWallet();
        this.isGetToken = true;
        setTimeout(async () => {
          this.anmiate1 = false;
          await checkUserExist({
            userAddress: this.$store.state.currentAddress
          }).then(async re => {
            if (re.data.data == "") {
              this.animate3 = true;
            } else {
              this.animate2 = true;
            }
          });
        }, 2000);
      }
    }
  },
  methods: {
    async empower(opt) {
      if (opt == 2) {
        if (this.EmpowerSignForm.sp1 == "" || this.EmpowerSignForm.sp2 == "" || this.EmpowerSignForm.sp3 == "" || this.EmpowerSignForm.sp4 == "" || this.EmpowerSignForm.sp5 == "" || this.user.encryptedPassword == "") {
          this.$notify({
            title: "信息不能为空",
            type: "warning",
            position: "top-left",
            offset: 200
          });
          return;
        }
      }
      this.user.encryptedPassword = CryptoJS.SHA256(this.user.encryptedPassword).toString();
      await setMnemonic(this.user);
      this.EmpowerSignForm.userAddress = this.$store.state.currentAddress;
      await setAuthenticationMetaInformation(this.EmpowerSignForm);
      await getToken(this.user).then(async re => {
        localStorage.clear();
        if (re.data.data == null) {
          this.$notify({
            title: "密码错误",
            type: "waring",
            position: "top-left",
            offset: 200
          });
        } else {
          localStorage.setItem("token", re.data.data);
          let currentAddress = {
            ownerAddress: this.EmpowerSignForm.userAddress
          };
          await postOwnerContractList(currentAddress).then(re => {
            this.$store.commit("setOwnerNFTList", re.data.data);
          });
          this.giveChatInitToWalletConnect();
          this.$store.commit("connection", true);
          this.$store.commit("setEmpower", true);
          this.isGetToken = false;
          this.$notify({
            title: "🎉 连接成功",
            position: "top-left",
            offset: 200
          });
        }
      });
    },
    openEmpower() {
      if (this.$store.state.isEmpower || window.ethereum == undefined) {
        return;
      }
      if (!this.isUnlocked) {
        this.$notify({
          title: "钱包未解锁",
          type: "warning",
          position: "top-left",
          offset: 200
        });
        return;
      } else {
        this.isGetToken = true;
      }
    },
    async checkEmpower() {
      this.user.encryptedPassword = CryptoJS.SHA256(this.user.encryptedPassword).toString();
      await getToken(this.user).then(re => {
        localStorage.clear();
        if (re.data.data == null) {
          this.$notify({
            title: "授权码错误",
            type: "warning",
            position: "top-left",
            offset: 200
          });
        } else {
          let currentAddress = {
            ownerAddress: this.$store.state.currentAddress
          };
          postOwnerContractList(currentAddress).then(re => {
            this.$store.commit("setOwnerNFTList", re.data.data);
          });
          localStorage.setItem("token", re.data.data);
          this.$store.commit("setEmpower", true);
          this.$store.commit("connection", true);
          this.giveChatInitToWalletConnect();
          this.isGetToken = false;
          this.$notify({
            title: "🎉 连接成功",
            position: "top-left",
            offset: 200
          });
        }
      });
    },
    viewControl(opt) {
      switch (opt) {
        case 1:
          this.animate2 = false;
          this.animate4 = true;
          break;
        case 2:
          this.animate2 = true;
          this.animate4 = false;
          break;
        case 3:
          this.animate2 = true;
          this.animate5 = false;
          break;
        default:
          this.animate2 = false;
          this.isGetToken = true;
          this.anmiate1 = false;
          this.animate4 = true;
          this.user.encryptedPassword = "";
          break;
      }
    },
    async backPassword() {
      this.EmpowerSignForm.userAddress = this.$store.state.currentAddress;
      forgetMnemonic(this.EmpowerSignForm).then(re => {
        if (re.data.data) {
          this.animate5 = true;
          this.animate4 = false;
          this.$notify({
            title: "密保正确",
            type: "success",
            position: "top-left",
            offset: 200
          });
        } else {
          this.$notify({
            title: "密保错误",
            type: "warning",
            position: "top-left",
            offset: 200
          });
        }
      });
    },
    async resetMnemonic() {
      this.EmpowerSignForm.newMnemonic = CryptoJS.SHA256(this.newMnemonic).toString();
      this.EmpowerSignForm.userAddress = this.$store.state.currentAddress;
      await checkUserExist(this.EmpowerSignForm).then(re => {
        if (re.data.data == this.EmpowerSignForm.encryptedPassword) {
          this.$notify({
            title: "不能与旧密码重复",
            type: "warning",
            position: "top-left",
            offset: 200
          });
          return;
        } else {
          resetMnemonic(this.EmpowerSignForm).then(re => {
            if (re.data.data == null) {
              this.$notify.error({
                title: "系统异常",
                position: "top-left",
                offset: 200
              });
            }
            this.$notify({
              title: "🎉 重设成功",
              position: "top-left",
              offset: 200
            });
            this.animate2 = true;
            this.animate5 = false;
          });
        }
      });
    },
    canacelEmpover() {
      this.isGetToken = false;
      this.$store.commit("connection", true);
    },
    giveChatInitToWalletConnect() {
      console.log("聊天栏正初始化.......");
      this.$refs.ChatMemu.init();
    },
    setEchartChange(data) {
      this.echartChange = data;
      console.log(this.echartChange);
    },
    async connectWallet() {
      if (localStorage.getItem["token"] == null || !this.$store.state.isconnect) {
        try {
          // 请求用户授权
          await window.ethereum.request({
            method: "eth_requestAccounts"
          }).then(async handleAccountsChanged => {
            this.$store.commit("setcurrentAddress", handleAccountsChanged[0]);
            this.$store.commit("changeAvatar", handleAccountsChanged[0]);
            this.user.userAddress = handleAccountsChanged[0];
          }).catch(error => {
            this.$store.commit("connection", false);
            if (error.code === 4001) {
              // EIP-1193 userRejectedRequest error
              console.log("Please connect to MetaMask.");
            } else {
              console.error(error);
            }
          });
        } catch (error) {
          console.error(error);
          this.$notify.error({
            title: "连接失败",
            position: "top-left",
            offset: 200
          });
        }
      }
    },
    Copy() {
      navigator.clipboard.writeText(this.$store.state.currentAddress).then(() => {
        this.isCopy = true;
        setTimeout(() => {
          this.isCopy = false;
        }, 3000);
      });
    },
    GETHashAvatar() {
      if (this.$store.state.isconnect) {
        this.avatar = "data:image/png;base64," + new this.Identicon(this.$store.state.currentAddress, 120).toString();
        this.$refs.avatar.width = 60;
        this.$refs.avatar.height = 60;
      } else {
        this.avatar = require("@/assets/webAssets/MetaMask.png");
        this.$refs.avatar.width = 40;
        this.$refs.avatar.height = 40;
      }
    }
  }
};
