import Vue from 'vue'
import Vuex from 'vuex'


Vue.use(Vuex)

export default new Vuex.Store({
  namespaced:true,
  state: {
    isconnect: false,
    isDark:false,
    currentAddress:"",
    Web3:null,
    ipfs: null,
  },
  getters: {
  },
  mutations: {
    connection(state,value) {
      state.isconnect=value

    },
    setcurrentAddress(state,value) {
      state.currentAddress=value

    },
    setWEB3(state,value){
      state.Web3=value

    },
    setIPFS(state,value){
      state.ipfs=value;

    },
    setIsDark(state, value) {
      state.isDark = value;
    }
  },
  actions: {


  },
  modules: {
  }
})
