import Vue from 'vue'
import VueRouter from 'vue-router'
import MarketShopIndex from '@/views/market/index.vue'

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    redirect: '/home/MarketShop' 
  },
  {
    path: '/home',
    name: 'home',
    component: MarketShopIndex,
    // 
    children:[
      {
        path:'ToSell',
        name:'ToSell',
        component: () => import('@/views/user/ToSellView.vue'),
        // meta: { isAuth: true}
      },
      {
        path:'ImitNFT',
        name:'ImitNFT',
        component: () => import('@/views/market/imitNFT.vue'),
      },
      {
        path:'MarketShop',
        name:'MarketShop',
        component:() => import('@/views/market/NFTMarket.vue')
      },
      {
        path:'NFTInf',
        name:'NFTInf',
        component:() => import('@/views/market/NFTInf.vue')
      },
      {
        path:'searchNFT',
        name:'searchNFT',
        component:() => import('@/views/market/searchNFT.vue')
      }
    ]
  }
]

const router = new VueRouter({
  routes
})

//全局前置路由守卫————初始化的时候被调用、每次路由切换之前被调用
router.beforeEach((to, from, next) => {
  //如果路由需要跳转
  if (to.meta.isAuth) {
      //如果钱包已经连接，放行
      // if (router.app.$options.store.state.isconnect) {
          next() 
      // } 
  } else {
      // 否则，不放行
      // router.push("/")
      next() 
  }
})
export default router
