import Vue from 'vue'
import App from './App.vue'
import router from './router'
import store from './store'
import Web3 from 'web3'
Vue.config.productionTip = false
import 'animate.css';
import './plugins/element.js'
import Identicon from "identicon.js"
import axios from 'axios'
Vue.prototype.axios = axios
Vue.prototype.Web3 = Web3
Vue.prototype.Identicon = Identicon
const eventBus = new Vue();
export default eventBus;
new Vue({
  Web3,
  router,
  store,
  render: h => h(App)
}).$mount('#app')

import { startSpinning } from "url-spinner";
// Perform some async tasks and call stopSpinning at a proper time
startSpinning({
  duration: 3000,
  spinner: ["🌚", "🌘", "🌗", "🌖", "🌝", "🌔", "🌓", "🌒"]
});
var currentTime = new Date().getTime(); 

// 获取当前日期的年月日
var year = new Date().getFullYear();
var month = new Date().getMonth() + 1;
var day = new Date().getDate();

// 获取第二天0点的时间戳（以毫秒为单位）
var nextDay = new Date(year, month-1, day+1).getTime();

// 计算当前时间与第二天0点的毫秒差
var diff = nextDay - currentTime;
