import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    redirect: '/home/MarketShop'
  },
  {
    path: '/home',
    name: 'home',
    component: () => import('@/views/market/index.vue'),
    // 
    children: [
      {
        path: 'ToSell',
        name: 'ToSell',
        component: () => import('@/views/user/ToSellView.vue'),
        meta: { isAuth: true}
      },
      {
        path: 'ImitNFT',
        name: 'ImitNFT',
        component: () => import('@/views/market/imitNFT.vue'),
      },
      {
        path: 'addImit',
        name: 'addImit',
        component: () => import('@/views/market/addMint.vue'),
      },
      {
        path: 'MarketShop',
        name: 'MarketShop',
        component: () => import('@/views/market/NFTMarket.vue'),
      },
      {
        path: 'NFTInf',
        name: 'NFTInf',
        component: () => import('@/views/market/NFTInf.vue')
      },

    ]
  },
  {
    path: '/userhome',
    name: 'userhome',
    component: () => import('@/views/user/UserHome.vue')
  }
]

const router = new VueRouter({
  routes
})

//全局前置路由守卫————初始化的时候被调用、每次路由切换之前被调用
router.beforeEach((to, from, next) => {
  //如果路由需要跳转
  if (to.meta.isAuth) {
    //如果钱包已经连接，放行
    // if (router.app.$options.store.state.isconnect) {
    //   next()
    // }
    next()
  } else {
    // 否则，不放行
    router.push("/home/MarketShop")
  }
})
export default router
